<script>
    $("#add").click(function () {
        var treeList = $("a");
        console.log('treeList', treeList);
    });

    var auditPaperList = [];
    var activePdf = '';
    var arrayCollection = [
        {
            "id": "0",
            "content_id": "content_0_1",
            "parent": "#",
            "text": "প্রথম পৃষ্ঠা",
            "content": '<p style="text-align:center"><br></p><p style="text-align:center"><br></p><p style="text-align:center"><br></p><p style="text-align:center"><img src="data:image/png;base64," data-filename="logo.png" style="width:120px"></p><p style="text-align:center"><br></p><h1 style="text-align:center"><span style="color:#222" arial="">Audit Plan</span><span style="color:#222" arial="">(নিরীক্ষা পরিকল্পনা)</span></h1><h1 style="text-align:center"><span style="color:#222" arial=""><br></span></h1><h1 style="text-align:center"><span style="color:#222" arial=""><br></span></h1><h3 style="text-align:center"><span arial=""><font color="#222222">সিভিল অডিট অধিদপ্তর&nbsp;&nbsp;</font></span></h3><h3 style="text-align:center"><span arial=""><font color="#222222"><br></font></span></h3><h3 style="text-align:center"><span arial=""><font color="#222222"><br></font></span></h3><h3 style="text-align:center"><span arial=""><font color="#222222">মঞ্জুরী নং-২৮</font></span></h3><h3 style="text-align:center"><span arial=""><font color="#222222">শ্রম ও কর্মসংস্থান&nbsp; মন্ত্রণালয় এবং এর নিয়ন্ত্রনাধীন প্রতিষ্ঠানসমূহের<br></font></span></h3><h3 style="text-align:center"><span arial=""><font color="#222222"><br></font></span></h3><h3 style="text-align:center"><span arial=""><font color="#222222"><br></font></span></h3><h5 style="text-align:center"><span arial=""><font color="#222222">অর্থবছরঃ ২০১৮-২০১৯&nbsp;</font></span></h5><h5 style="text-align:center"><span arial=""><font color="#222222"><br></font></span></h5><h5 style="text-align:center"><span arial=""><font color="#222222"><br></font></span></h5><h5 style="text-align:center"><span arial=""><font color="#222222">&nbsp; &nbsp;সিভিল অডিট অধিদপ্তর&nbsp;&nbsp;<br></font></span></h5><p style="text-align:center"><br></p>'
        },
        {"id": "1", "content_id": "content_0_2", "parent": "#", "text": "সূচিপত্র", "content": ""},
        {"id": "2", "content_id": "content_1", "parent": "#", "text": "১.০ ভূমিকা", "content": ""},
        {"id": "3", "content_id": "content_1_1", "parent": "1", "text": "১.১ পটভূমি", "content": ""},
        {"id": "4", "content_id": "content_1_2", "parent": "1", "text": "১.২ এনটিটি'র নাম", "content": ""},
        {"id": "5", "content_id": "content_1_3", "parent": "1", "text": "১.৩ নিরীক্ষার ধরণ", "content": ""},
        {"id": "6", "content_id": "content_2", "parent": "#", "text": "২.০ প্রতিষ্ঠান পরিচিতি", "content": ""},
        {"id": "7", "content_id": "content_2_1", "parent": "2", "text": "২.১ ভূমিকা", "content": ""},
        {"id": "8", "content_id": "content_2_2", "parent": "2", "text": "২.২ অর্গানরগ্রাম", "content": ""},
        {"id": "9", "content_id": "content_2_3", "parent": "2", "text": "২.৩ সুপ্রীম কোর্টের মিশন", "content": ""},
        {"id": "10", "content_id": "content_2_4", "parent": "2", "text": "২.৪ সাংগঠনিক কাথামো/জনবল", "content": ""},
        {
            "id": "11",
            "content_id": "content_2_5",
            "parent": "2",
            "text": "২.৫ সুপ্রীম কোর্টের কার্যাবলী",
            "content": ""
        },
        {"id": "12", "content_id": "content_2_6", "parent": "2", "text": "২.৬ প্রধান কর্মকৃতি", "content": ""},
    ];


    $('#createPlanJsTree').jstree({
        "core": {
            "check_callback": true,
            'data': arrayCollection
        },
        "plugins": ["dnd", "checkbox", "search"]
    });

    for (let i = 0; i < arrayCollection.length; i++) {
        var arrayData = {"id": arrayCollection[i].id, "title": arrayCollection[i].text, "content": ""}
        auditPaperList.push(arrayData);
        var dataHtml = '<div class="pdf-screen"><p class="pageTileNumber">' + arrayCollection[i].text + '</p><div id="pdfContent_' + arrayCollection[i].content_id + '">' + arrayCollection[i].content + '</div></div>';
        $("#writing-screen-wrapper").append(dataHtml);
    }

    $('#createPlanJsTree').on("select_node.jstree", function (e, data) {
        activePdf = data.node.id;

        arrayCollection.map(function (value, index) {
            if (value.id == activePdf) {
                var content = value.content;
                $("#pdfContent_" + value.content_id).html(content);
                $('.note-editable').html(content);
            } else {
                $('.summernote').summernote('reset');
            }
        });
    });

    function checkIdAndSetContent(content) {
        arrayCollection.map(function (value, index) {
            if (value.id == activePdf) {
                value.content = content;
                $("#pdfContent_" + value.content_id).html(content);
            }
        });
    }

    $('.summernote').summernote({
        height: 600,
        callbacks: {
            onChange: function (contents, arrayCollection) {
                if ($("#createPlanJsTree").jstree("get_selected").length == '0') {
                    alert('Plz select page');
                } else {
                    checkIdAndSetContent(contents);
                }
            }
        }
    });

    Split(['#split-0', '#split-1', '#split-2'], {
        minSize: 150,
        snapOffset: 10,
        gutterSize: 5,
    });

</script>
